jQuery.fn.extend({createTableFlod:function(obj){var html=obj.text||'';var speed=obj.speed||200;var tr=$(this);tr.off('click').on('click',function(){var trthis=$(this);$(".yt-table tr").removeClass("yt-table-active");var tds=$(this).find('td');var trtext='';if(typeof html=='function'){trtext=html($(trthis))}else{trtext=html}var trdiv=$('<tr class="fold-unfold"><td colspan="'+tds.length+'" style="padding:0;"><div class="fold-div">'+trtext+'</div></td></tr>');if(!$(this).next().hasClass('fold-unfold')){$('.fold-unfold').remove();$(trdiv).find('.fold-div').css('display','none');$(trdiv).find('.fold-div').slideDown(speed,function(){if(trtext!=""){trthis.after(trdiv)}})}else{$(trdiv).find('.fold-div').slideUp(speed,function(){$(trthis).next('.fold-unfold').remove();$(trthis).removeClass("yt-table-active")})}})}});